import { useContext } from "react";
import classes from "./MainNavigation.module.css";
import { NavLink, useRouteLoaderData, Form } from "react-router-dom";
import WishContext from "../store/wish-context";

function MainNavigation() {
  const token = useRouteLoaderData("root");

  const wishCtx = useContext(WishContext);

  const numberOfWishHosts = wishCtx.hosts.reduce((curNumber, host)=>{
    return (curNumber+host.amout); // 현재 값에서 + item 의 양
  },0);

  return (
    <header className={classes.header}>
      <nav>
        <ul>
          <li>
            <NavLink to="/">
              <div className={classes.logo}>WWOOF KOREA</div>
            </NavLink>
          </li>
          <li>
            <NavLink to="hosts">호스트 찾기</NavLink>
          </li>
        </ul>
      </nav>
      <nav>
        <ul>
          <li>
            <NavLink to="/">
              <span>위시리스트</span>
              <span>{numberOfWishHosts}</span>
            </NavLink>
          </li>
          {!token && (
            <li>
              <NavLink to="login">로그인</NavLink>
            </li>
          )}

          {token && (
            <li>
              <NavLink to="mypage">마이페이지</NavLink>
            </li>
          )}
          {token && (
            <li>
              <Form action="/logout" method="post">
                <button className={classes.button}>로그아웃</button>
              </Form>
            </li>
          )}
        </ul>
      </nav>
    </header>
  );
}

export default MainNavigation;
